import React, { useState } from 'react'

const AddCountryForm = props => {
	const initialFormState = { id: null, name: '', capital: '' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	return (
		<form
			onSubmit={event => {
				event.preventDefault()
				if (!user.name || !user.capital) return

				props.addUser(user)
				setUser(initialFormState)
			}}
		>
			<label>Name</label>
			<input type="text" name="name" value={user.name} onChange={handleInputChange} />
			<p><label>Capital</label>
			<input type="text" name="capital" value={user.capital} onChange={handleInputChange} /></p>
			<p><button>Add new country</button></p>
		</form>
	)
}

export default AddCountryForm
