import React, { useState, Fragment } from 'react'
import { BrowserRouter as Router } from "react-router-dom";
import AddCountryForm from './forms/AddCountryForm'
import EditCountryForm from './forms/EditCountryForm'
import CountryTable from './tables/CountryTable'

const App = () => {
	// Data
	const usersData = [
		{ id: 1, name: 'Greece', capital: 'Athens' },
		{ id: 2, name: 'Italy', capital: 'Rome' },
		{ id: 3, name: 'Hungary', capital: 'Budapest' },
		{ id: 4, name: 'Spain', capital: 'Madrid' },
		{ id: 5, name: 'Germany', capital: 'Berlin' },
		{ id: 6, name: 'Denmark', capital: 'Copenhagen' },
		{ id: 7, name: 'Portugal', capital: 'Lisbon' },
		{ id: 8, name: 'Russia', capital: 'Moscow' },
		{ id: 9, name: 'France', capital: 'Paris' },
		{ id: 10, name: 'Norway', capital: 'Oslo' },
	]

	const initialFormState = { id: null, name: '', capital: '' }

	// Setting state
	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	// CRUD operations
	const addUser = user => {
		user.id = users.length + 1
		setUsers([ ...users, user ])
	}

	const deleteUser = id => {
		setEditing(false)

		setUsers(users.filter(user => user.id !== id))
	}

	const updateUser = (id, updatedUser) => {
		setEditing(false)

		setUsers(users.map(user => (user.id === id ? updatedUser : user)))
	}

	const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, name: user.name, capital: user.capital })
	}

	return (
	<Router>
		<div className="container">
			<h1 id = "hed1">Countries App</h1>
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<h2>Edit country</h2>
							<EditCountryForm
								editing={editing}
								setEditing={setEditing}
								currentUser={currentUser}
								updateUser={updateUser}
							/>
						</Fragment>
					) : (
						<Fragment>
							<h2>Add country</h2>
							<AddCountryForm addUser={addUser} />
						</Fragment>
					)}
				</div>

					<div className="flex-large">
						<h2>View Countries</h2>
						<CountryTable users={users} editRow={editRow} deleteUser={deleteUser} />
					</div>
			</div>
		</div>
	</Router>
	)
}

export default App
